import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Main.css";

import Button from "../../components/Button/Button";
import TextBox from "../../components/TextBox/TextBox";
import { Pick, PickList } from "../../components/PickList/PickList";

export default function Main() {
  const [likeList, setLikeList] = useState([]);
  const [dislikeList, setDislikeList] = useState([]);

  console.log(typeof likeList);
  console.log(typeof [1, 2, 3]);

  const setLike = (name) => {
    const tmp = likeList.push(name);
    setLikeList(tmp);
  };

  const setDislike = (name) => {
    const tmp = dislikeList.push(name);
    setDislikeList(tmp);
  };

  return (
    <div className="main">
      <div>
        <h1> 오늘 뭐 먹지? </h1>
        <div className="submitBox">
          <table className="inputTable">
            <tbody>
              <TextBox
                text="호"
                color={"blue"}
                placeholder={"키워드를 작성하고 Enter를 누르세요! 예) 김치"}
                func={setLike}
              />
              <PickList name="like">
                {likeList.map((item) => (
                  <Pick name={item} />
                ))}
              </PickList>
              <TextBox
                text="불호"
                color={"red"}
                placeholder={"키워드를 작성하고 Enter를 누르세요! 예) 탕"}
                func={setDislike}
              />
              <PickList name="dislike" />
            </tbody>
          </table>
          <Button text="뭐 먹지?" />
        </div>
        {/* <Link to="/">main</Link>
        <Link to="/result">result</Link> */}
      </div>
    </div>
  );
}
