import React from "react";
import "./TextBox.css";

export default function TextBox({ text, color, placeholder, list }) {
  const add = (e) => {
    if (e.key === "Enter") {
      const value = e.target.value;

      if (value === "" || value === undefined) {
        alert("값을 입력해주세요!");
        return;
      }

      props.list.push(e.target.value);
    }
  };

  return (
    <tr className="TextBox">
      <td>
        <p className={color}>{text}</p>
      </td>
      <td>
        <input
          className={color}
          type="text"
          placeholder={placeholder}
          onKeyDown={add}
        ></input>
      </td>
    </tr>
  );
}
